import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Book } from '../book';

@Component({
  selector: 'app-book-details',
  templateUrl: './book-details.component.html',
  styleUrls: ['./book-details.component.css']
})
export class BookDetailsComponent {

  constructor(private router:Router){}

  book: Book = history.state.response;

  backToHome(){
    this.router.navigateByUrl('/home');
  }
}
<div class="container">
    <h2> Book Details</h2>
        <div class="form-group">
            <label for="title">Id:</label> {{book.id}}
            <label for="title">Title:</label> {{book.title}}
            <label for="author">Author:</label> {{book.author}}
            <label for="year">Year:</label> {{book.year}}
        </div> <br>
        <button class="btn btn-danger" (click)="backToHome()">Back</button>
</div>
