import { Component, OnInit } from '@angular/core';
import { Book } from '../book';
import { BookService } from '../book.service';

@Component({
  selector: 'app-book-page',
  templateUrl: './book-page.component.html',
  styleUrls: ['./book-page.component.css']
})
export class BookPageComponent implements OnInit {
  books: Book[] = [];
  id = '';
  newBook: Book = new Book();
  message: any;

  constructor( private bookService: BookService) { }

  ngOnInit(): void {
    this.fetchBooks();
  }

  fetchBooks(){
    this.bookService.getBooks().subscribe( (response) => {
      this.books = response;
      console.log(this.books);
    },
    (error) => {
      console.error("Error occured in getBooks()");
      this.message = "Error occured in fetchBooks()";;
    }
    );
  }

  findBook(){
    this.bookService.searchBook(this.id).subscribe( (response) =>{
      this.books = [];
      this.books = [response];
    },
    (error) => {
      console.error("Error occured in getBooks()");
      if(error.status == 404) this.message = "Book with ID: "+this.id+" is not present";
      else this.message = "Error occured in getBooks()";
    })
  }

  // addBook(){
  //   this.bookService.addBook(this.newBook).subscribe(() =>{
  //     this.fetchBooks();
  //      this.newBook = new Book();
  //   });
  // }
  addBook(){
    this.bookService.addBook(this.newBook).subscribe( (response) =>{
      if(response.status == 200) this.fetchBooks;
    },
    (error) => {
      console.error("Error occured in getBooks()");
      if(error.status == 404) this.message = "Book with ID: "+this.id+" is not present";
      else this.message = "Error occured in getBooks()";
    })
  }


}
